import React, { useEffect, useState, useContext } from 'react';
import CoinRow from './portfolio/CoinRow';
import { axiosInstance } from '../api/axios';
import axios from 'axios';
import styled from 'styled-components';
import { MyContext } from '../contextAPI/UserContext';

const Portfolio = () => {
    const { user } = useContext(MyContext)
    const [holdings, setHoldings] = useState([])
    const [total, setTotal] = useState(null)
    const [res, setRes] = useState(null)
    const [mergedArr, setMergedArr] = useState(null)
    const [display, setDisplay] = useState(false)

    // console.log(user)
    useEffect(() => {
        async function getData() {
            try {
                let { data } = await axiosInstance.get(`/portfolio/`, {
                    params: {
                        // ID IS HARD CODED PASS IN CORRECT DATA
                        id: user
                    }
                })
                setHoldings(data)
                let str = ""
                data.forEach((e, i) => {
                    if (i !== data.length - 1) {
                        str += e.name.toLowerCase() + "%2C%20"
                    } else {
                        str += e.name.toLowerCase()
                    }
                })

                let res = await axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=${str}`)
                setRes(res.data)
            } catch (error) {
                console.log(error)
            }
        }
        getData()
    }, [total])

    // const myArrayFilter = coins.filter(coin => {
    //     return holdings.some(holding => {
    //         return holding.name === coin.name
    //     })
    // })

    const calcHandler = () => {
        const a3 = res.map(t1 => ({ ...t1, ...holdings.find(t2 => t2.name === t1.name) }))
        setMergedArr(a3)
        setDisplay(true)
    }

    const calculateSum = () => {
        let sum = mergedArr.reduce((a, b) => a + (b.quantity * parseFloat(b.current_price)), 0)
        setTotal(sum.toFixed(2))
    }

    useEffect(() => {
        if (display === true) {
            console.log('inside useeffect -- true')
            calculateSum()
        }
    }, [display])

    return (
        <>
            {holdings && (
                <div>
                    <PortfolioH1>My Portfolio</PortfolioH1>
                    {/* {total && <h1>${total}</h1>} */}
                    {holdings?.map((holding, i) => (
                        <CoinRow
                            key={i}
                            name={holding.name}
                            symbol={holding.symbol}
                            quantity={holding.quantity}
                            image={holding.image_url}
                            id={holding.id}
                            user_id={holding.user}
                            holdings={holdings}
                            setHoldings={setHoldings}
                        />
                    ))}
                    {total && <TotalH2><span className="est">â‰ˆ </span><span className="total">${total}</span></TotalH2>}
                    <StyledButton onClick={calcHandler}>Calculate</StyledButton>
                </div >
            )}
        </>
    )
}

export default Portfolio

const StyledButton = styled.button`
    margin-top: 2rem;
    border-radius: 4px;
    border: 1px solid;
    padding: 8px 8px;
    width: 5rem;
    cursor: pointer;
    background-color: #0b5351;
    color: white;

    &:hover {
        transform: scale(1.1);
        transition: all 0.5s ease;
    }


`

const PortfolioH1 = styled.h1`
    padding-top: 3rem;
    padding-bottom: 3rem;
`

const TotalH2 = styled.h2`
    padding-top: 3rem;
    /* padding-bottom: 1rem;/ */

    .total {
        font-weight: 400;
    }


`
