import React, { useEffect, useState } from 'react'
import { axiosInstance } from '../api/axios';
import Carbchain from '../components/blockchain/Carbchain';
import styled from 'styled-components'

const Blockchain = () => {
    const [wallet, setWallet] = useState([])
    useEffect(() => {
        async function getData() {
            let { data } = await axiosInstance.get('http://localhost:8000/api/wallet/balance/')
            setWallet(data)
        }
        getData()
    }, [])

    return (
        <BlockchainContainer>
            {/* <pre>{JSON.stringify(wallet, null, 2)}</pre> */}
            <h1>Address: {wallet.address}</h1>
            <h1>Balance: {wallet.balance}</h1>
            <Carbchain />
        </BlockchainContainer>
    )
}

export default Blockchain

const BlockchainContainer = styled.section`
    /* background-color: gray; */
    padding: 0.5rem calc((100vw - 1200px) / 2);
    border: none;
    background-color: #121212;
`
