import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom'

const NewsCard = ({ data }) => {
  function truncate(str, n) {
    return str?.length > n ? str.substr(0, n - 1) + "..." : str;
  }
  console.log(data)
  return (
    <CardContainer>
      {data.map((el, i) => (
        <Card key={i}>
          <ImageContainer>
            <img src={el.image_url} />
          </ImageContainer>
          <CardContent>
            <CardTitle>
              <h3>{el.title}</h3>
            </CardTitle>
            <CardBody>
              <p>{truncate(el.text, 100)}</p>
            </CardBody>
          </CardContent>
          <ButtonContainer>
            <button>
              <a href={el.news_url} target="_blank">
                <a>Read More</a>
              </a>
            </button>
          </ButtonContainer>
        </Card>
      ))}
    </CardContainer>
  )
}

export default NewsCard

const CardContainer = styled.section`
    /* margin-top: 3rem; */
    display: grid;
    grid-gap: 30px;
    grid-template-columns: minmax(275px, 1fr) minmax(275px, 1fr) minmax(275px, 1fr);
    /* padding-left: 1rem;
    padding-right: 1rem; */
`
const Card = styled.div`
    display: flex;
    flex-direction: column;
    /* flex: auto; */
    position: relative;
    background-position: center center;
    /* max-width: 366.6px; */
    /* padding-left: 1rem;
    padding-right: 1rem; */
  /* object-fit: contain; */
  /* margin: 16px; */
  overflow: hidden;
  box-shadow: 0px 0px 15px -5px;
  transition: 0.5s;
  border-radius: 8px;
  background-color: #E0E0E0;
  /* &:hover {
    transform: scale(1.1);
    box-shadow: 0px 0px 15px 0px;
  } */
`;

const ImageContainer = styled.div`
  object-fit: contain;
  overflow: hidden;
  height: 250px;

  img {
    /* transform: scale(1); */
    transition: transform 0.5s ease;
    object-fit: cover;
    width: 100%;
    height: 100%;

    /* &:hover {
      transform: scale(1.3);
    } */
  }
`;

const CardContent = styled.div`
  margin: 1rem;
  margin-top: 0.5rem;
`;
const CardTitle = styled.div`
  margin-bottom: 0.5rem;
  height: 110px;

  h3 {
    margin: 0;
    padding: 0;
  }
`;

const CardBody = styled.div`
  /* position: relative; */
  background-color: white;
  display: flex;
  /* margin-top: auto; */
  height: 60px;
  overflow: hidden;

  p {
    margin: 0;
    padding: 0;
  }
`;
const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  /* vertical-align: bottom; */
  margin-top: auto;

  button {
    padding: 0.5rem;
    background-color: white;
    border: none;
    transition: 0.2s;
    margin-bottom: 0.5rem;
  }

  a {
    text-transform: uppercase;
    /* color: #092327; */
    color: white;
    font-weight: bold;
    text-decoration: none;
    background-color: #0b5351;
    padding: 0.5rem;
    border-radius: 4px;
  }
`;
