import React from 'react'
import NewsPost from './NewsPost';
import styled from 'styled-components';

const PostCollage = ({ posts, columns, tags }) => {
    // style={{ gridTemplateColumns: `repeat(${columns}), minmax(275px, 1fr))` }}
    return (
        <CollageContainer className="collage" >
            {posts?.map((post, index) => (
                <NewsPost {...{ post, index, tags, key: index }} />
            ))}
        </CollageContainer>
    )
}

export default PostCollage

const CollageContainer = styled.section`

    grid-template-columns: repeat(3);
    grid-template-columns: minmax(275px, 1fr);
    .collage {
        display: grid;
        grid-gap: 30px;
    }


`
