import React, { useState, useEffect, useContext } from 'react';
import { coinGecko } from '../../api/axios';
import Coin from './Coin';
import styled from 'styled-components';
import { MyContext } from '../../contextAPI/UserContext';
import Detail from '../../pages/Detail';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';
import { useLocation } from 'react-router-dom'
import { fadeIn } from '../../animations/animations'

const CryptoData = () => {
  // const [coins, setCoins] = useState([])
  const [search, setSearch] = useState("")
  const { coins, setCoins } = useContext(MyContext)
  const location = useLocation()
  let pathId = location.pathname.split("/")[2]

  useEffect(() => {
    const getData = async () => {
      let { data } = await coinGecko.get("/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false")
      setCoins(data)
    }
    try {
      getData()
    } catch (error) {
      console.log(error)
    }
  }, [])

  const filteredCoins = coins.filter(coin => coin.name.toLowerCase().includes(search.toLowerCase()))

  const onChangeHandler = (e) => {
    setSearch(e.target.value)
  }

  return (
    <div>
      {/* {pathId && <Detail />} */}
      <FormContainer>
        <form class="form">
          <h1>Search your currency</h1>
          <input name="search" className="form__input" type="text" onChange={onChangeHandler}></input>
        </form>
      </FormContainer>
      <CoinList>
        <AnimateSharedLayout type="crossfade">
          <AnimatePresence>
            <Detail pathId={pathId} />
          </AnimatePresence>
          {filteredCoins.map(coin => {
            return (
              <Coin key={coin.id}
                id={coin.id}
                name={coin.name}
                image={coin.image}
                symbol={coin.symbol}
                volume={coin.market_cap}
                price={coin.current_price}
                priceChange={coin.price_change_percentage_24h}
              />
            )
          })}
        </AnimateSharedLayout>
      </CoinList>
    </div>
  )
}

export default CryptoData


const FormContainer = styled.div`
  input {
    border-radius: 4px;
    border: 1px solid black;
    height: 20px;
    width: 10rem;
    margin-bottom: 1rem;

    &:focus {
      outline: none;
    }
  }
`

const CoinList = styled(motion.div)`

`
