import React, { useContext } from 'react';
import { axiosInstance } from '../../api/axios';
import { useForm } from 'react-hook-form'
import { UserContext } from '../../contextAPI/UserContext';
import styled from 'styled-components';

const LoginForm = () => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm()
    const { user, setUser } = useContext(UserContext)

    const loginHandler = async (d) => {
        try {
            const response = await axiosInstance.post('/token/obtain/', d)
            if (response.status === 200) {
                localStorage.setItem("access_token", response.data.access)
                localStorage.setItem("refresh_token", response.data.refresh)
                axiosInstance.defaults.headers["Authorization"] = "JWT " + localStorage.getItem("access_token");

            } else {
                console.log("wrong info")
            }

            // setUser(true)
            // return response
        } catch (e) {
            // console.log(e);
        }


    }
    return (
        <LoginFormContainer>
            <form onSubmit={handleSubmit(loginHandler)}>
                <h1>Sign In</h1>

                <div className="inputs">
                    <input className="form__input" type="text" {...register("email")} />
                    <label htmlFor="email">Email</label>

                </div>

                <div className="inputs">
                    <input className="form__input" type="password" {...register("password")} />

                    <label>Password</label>
                </div>
                <input className="btn" type="submit" value="submit" />
            </form>
        </LoginFormContainer>
    )
}

export default LoginForm

const LoginFormContainer = styled.div`
    h1 {

        font-weight: 400;
        margin-bottom: 3rem;
    }

    form {
        width: 360px;
        padding: 4rem 2rem;
        box-shadow: 0 10px 25px rgba(92, 99, 105, 0.2);
        border-radius: 1rem;

    }

    .inputs {
        position: relative;
        height: 48px;
        margin-bottom: 1.5rem;
    }

    .form__input {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        font-size: 16; // change this
        border: 1px solid green; //change this
        border-radius: 0.5rem;
        outline: none;
        padding: 1rem;
        background: none;
        z-index: 1;


    }

`
