import React, { useState } from 'react'
import styled from 'styled-components'
import { axiosInstance } from '../../api/axios'
import { useHistory } from 'react-router-dom';

const CoinRow = ({ name, symbol, quantity, image, id, user_id, holdings, setHoldings }) => {
    // const [isDeleted, setIsDeleted] = useState(false)
    console.log(holdings)
    const newHoldings = { ...holdings }
    console.log(newHoldings)
    const editHandler = (e) => {
        e.preventDefault()
    }

    const deleteHandler = async (e) => {
        e.preventDefault()
        console.log(id)
        // try {
        //     let response = await axiosInstance.delete(`portfolio/${id}/`)
        //     console.log(response)
        //     // setIsDeleted(true)

        // } catch (error) {
        setHoldings(newHoldings)
        // }

    }
    return (
        <CoinRowContainer>
            <div className="row">

                <img src={image} alt="" />
                <a>{name}</a>
                <p>{symbol}</p>
                <p>{quantity}</p>
            </div>
            <div className="btns">
                <form onSubmit={editHandler}>
                    <input type="text" />
                    <button>Edit</button>
                </form>

                <form onSubmit={deleteHandler}>
                    <button>Delete</button>
                </form>
            </div>

        </CoinRowContainer>
    )
}

export default CoinRow

const CoinRowContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;


    .row {
        display: flex;
        align-items: center;
        /* padding-right: 24px; */

        a {
            font-size: 16px;
            width: 150px;
        }

        img {
            height: 30px;
            width: 30px;
            margin-right: 10px;
        }

    }

    .btns {
        display: flex;
        align-items: center;
    }
`
