import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom'

const NewsCard = ({ data }) => {
    function truncate(str, n) {
        return str?.length > n ? str.substr(0, n - 1) + "..." : str;
    }
    console.log(data)
    return (
        <CardContainer>
            {data.map((el, i) => (

                <Card>
                    <ImageContainer>
                        <img src={el.image_url} />
                    </ImageContainer>
                    <CardContent>
                        <CardTitle>
                            <h3>{el.title}</h3>
                        </CardTitle>
                        <CardBody>
                            <p>{truncate(el.text, 120)}</p>
                        </CardBody>
                    </CardContent>
                    <ButtonContainer>
                        <button>
                            <Link href="#">
                                <a>Continue Reading</a>
                            </Link>
                        </button>
                    </ButtonContainer>
                </Card>
            ))}
        </CardContainer>
    )
}

export default NewsCard

const CardContainer = styled.section`
    display: grid;
    grid-gap: 30px;
    grid-template-columns: minmax(275px, 1fr) minmax(275px, 1fr) minmax(275px, 1fr);
    padding-left: 1rem;
    padding-right: 1rem;
`
const Card = styled.div`
    display: flex;
    flex-direction: column;
    /* flex: auto; */
    position: relative;
    background-position: center center;
    /* max-width: 366.6px; */
    /* padding-left: 1rem;
    padding-right: 1rem; */
  /* object-fit: contain; */
  /* margin: 16px; */
  overflow: hidden;
  box-shadow: 0px 0px 15px -5px;
  transition: 0.5s;
  border-radius: 8px;
  /* &:hover {
    transform: scale(1.1);
    box-shadow: 0px 0px 15px 0px;
  } */
`;

const ImageContainer = styled.div`
  object-fit: contain;
  overflow: hidden;
  height: 200px;

  img {
    /* transform: scale(1); */
    transition: transform 0.5s ease;
    object-fit: cover;
    width: 100%;
    height: 100%;

    /* &:hover {
      transform: scale(1.3);
    } */
  }
`;

const CardContent = styled.div`
  margin: 1rem;
  margin-top: 0.5rem;
`;
