import React, { useState, useEffect } from 'react';
import NewsCard from '../components/news/NewsCard'
import axios from 'axios';
import styled from 'styled-components';
import PostCollage from '../components/news/PostCollage'
import FeaturedPosts from '../components/news/FeaturedPosts.js'
import NewsPost from '../components/news/NewsPost';

const News = () => {
  const [news, setNews] = useState([])
  const [featured, setFeatured] = useState([])
  const [message, setMesasge] = useState("")
  const [data, setData] = useState()

  useEffect(() => {
    async function getData() {
      let { data } = await axios.get('https://cryptonews-api.com/api/v1/category?section=general&items=5&token=vpwjwlmbytww0hjfywdtmlh29vkeugws7cesanuj')
      let res = await axios.get('https://cryptonews-api.com/api/v1?tickers=BTC,XRP,DOGE&items=4&token=vpwjwlmbytww0hjfywdtmlh29vkeugws7cesanuj')

      setNews(data.data)
      setFeatured(res.data.data)

    }
    getData()
  }, []);

  const searchHandler = async (e) => {
    e.preventDefault()
    let response = await axios.get(`https://cryptonews-api.com/api/v1?tickers=${e.target[0].value}&items=10&token=vpwjwlmbytww0hjfywdtmlh29vkeugws7cesanuj`)
    if (response.data.data.length !== 0) {
      setData(response.data.data)
    } else {
      setMesasge("I'm sorry nothing matches your criteria.")
    }

  }

  const lastPost = featured[featured.length - 1]
  return (
    <div>
      {news && lastPost && (
        <NewsCardContainer>
          <div className="row">
            <h1 className="heading">Recent News</h1>
            <section className="featured__posts">
              <FeaturedPosts posts={featured} />
              <NewsPost post={lastPost} />
            </section>
            <h1 className="second">Trending Topics</h1>
            <PostCollage posts={news} />
          </div>
          <div>
            <form onSubmit={searchHandler}>

              <input></input>
              <button>Search</button>
            </form>
          </div>
          {data ? (
            data.map((result) => (
              <CardContainer>
                <NewsCard
                  date={result.date}
                  image={result.image_url}
                  text={result.text}
                  title={result.title}

                />
              </CardContainer>
            ))
          )
            :
            <p>{message}</p>

          }
        </NewsCardContainer>)}
    </div>
  )
}

export default News

const NewsCardContainer = styled.section`
  max-width: 1200px;
  margin: 0 auto;

  .heading {
    width: 100%;
    color: black;

  }

  .row {
    margin: 4em 20px;

    .featured__posts {
      display: flex;
      flex-direction: row;
    }
  }

  h1 {
    text-align: left;
    font-size: 28px;
    font-weight: 500;
  }

  .second {
    padding-top: 1rem;
  }

  /* .overlay::before {
    z-index: 1;
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: linear-gradient(180deg, transparent 0%, transparent 18%, rgba(0,0,0,0.8) 99%, rgba(0,0,0,0.8) 100%)
  } */


`


const CardContainer = styled.div`

