import styled from "styled-components";
import Burger from "./Burger";
import { Link } from 'react-router-dom'
// import Logo from "../Logo";

const NavBar = () => {
  return (

    <NavBarContainer>
      <NavLink>
        {/* <Logo /> */}
        <h1 className="logo">CarbCoin</h1>
      <NavItems>
        <NavLink><Link to="/news">News</Link></NavLink>
        {/* <NavLink>Wallet</NavLink> */}
        <NavLink>CarbChain</NavLink>
        <NavLink><Link to="/cryptocurrencies">Cryptocurrencies</Link></NavLink>
      </NavItems>
      </NavLink>
      <Burger />
      <div>
          <NavLink>Login</NavLink>
      </div>
    </NavBarContainer>

  );
};

const NavBarContainer = styled.div`
  /* margin-top: -80px; // might wanna adjsut his for future projects */
  background-color: #092327;
  /* background: transparent; */
  color: #fff;
  height: 64px;
  display: flex;
  justify-content: space-between;
  padding: 0.5rem calc((100vw - 1000px) / 2);
  z-index: 10;

  .logo {
    font-family: 'Zen Dots', cursive;
    /* color: blue; */
  }
`;

const NavLink = styled.div`
  color: #fffffa;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 2rem;
  height: 100%;
  cursor: pointer;

  a {
    text-decoration: none;
    color: #fffffa;
  }

  &.active {
    color: #15cdfc;
  }
`;

const NavItems = styled.div`
  display: flex;
  align-items: center;

  @media screen and (max-width: 768px) {
    display: none;
  }
`;
export default NavBar;
