import { useContext, useState } from 'react';
import styled from "styled-components";
import Burger from "./Burger";
import { Link } from 'react-router-dom'
// import { UserContext } from "../../contextAPI/UserContext";
import { MyContext } from "../../contextAPI/UserContext";
import { useLocation } from 'react-router-dom';

const NavBar = () => {
  let location = useLocation()
  const [isOpen, setIsOpen] = useState(false)
  const { user } = useContext(MyContext)
  const [selection, setSelection] = useState(["Transactions", "Send CarbCoin"])

  return (

    <NavBarContainer location={location.pathname}>
      <NavLink>
        {/* <Logo /> */}
        <h2 className="logo">CarbCoin</h2>
        <NavItems>
          <NavLink>
            <Link to="/news">
              News
              </Link>
          </NavLink>
          <NavLink>
            {/* <Link to="/blockchain">
              CarbChain
              </Link> */}
            <p onClick={() => setIsOpen(!isOpen)}>Carbchain</p>
            {isOpen && (
              selection.map((item) => (
                <div className="dd-menu">
                  <p>{item}</p>
                </div>
              ))
            )}
          </NavLink>
          <NavLink>
            <Link to="/cryptocurrencies">
              Cryptocurrencies
              </Link>
            <div>

            </div>
          </NavLink>
        </NavItems>
      </NavLink>
      {/* <Burger /> */}
      <div>
        {!user ? (
          <NavLink>
            <Link to="/login">
              Login
            </Link>
          </NavLink>)
          :
          <NavLink>
            <Link to="/user/portfolio">
              My Portfolio
            </Link>
          </NavLink>
        }
      </div>
    </NavBarContainer>

  );
};

const NavBarContainer = styled.div`
  /* background: transparent; */
  /* margin-top: -80px; // might wanna adjsut his for future projects */
  background-color: #092327;
  /* background: transparent; */
  color: #fff;
  height: 64px;
  /* display: flex; */
  display: ${({ location }) => location === "/login/" || location === "/login" || location === "/register/" || location === "/register" ? "none" : "flex"};
  justify-content: space-between;
  padding: 0.5rem calc((100vw - 1200px) / 2);
  z-index: 10;

  .logo {
    font-family: 'Press Start 2P', cursive;
  }
`;

const NavLink = styled.div`
  color: #fffffa;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 2rem;
  height: 100%;
  cursor: pointer;

  a {
    text-decoration: none;
    color: #fffffa;
  }

  &.active {
    color: #15cdfc;
  }

  .dd-menu {
    display: flex;
    flex-direction: column;
  }
`;

const NavItems = styled.div`
  display: flex;
  align-items: center;

  @media screen and (max-width: 768px) {
    display: none;
  }
`;
export default NavBar;
