import React, { useState, useEffect } from 'react'
import { axiosInstance } from '../api/axios'

const SendCoin = () => {
    const [isSent, setIsSent] = useState(false)
    const [addresses, setAddresses] = useState([])

    useEffect(() => {
        async function getData() {
            try {
                let { data } = await axiosInstance.get('blockchain/addresses/')
                setAddresses(data)
                console.log(data)
            } catch (error) {
                console.log(error)
            }
            getData()
        }
    }, [])
    const submitHandler = async (e) => {
        e.preventDefault()
        let response = await axiosInstance.post('blockchain/transaction/', {
            recipient: e.target[0].value,
            amount: Number(e.target[1].value)
        })
        console.log(response)
        setIsSent(!isSent)
    }

    return (
        <div>
            <pre>{JSON.stringify(addresses, null, 2)}</pre>
            <form onSubmit={submitHandler}>
                <input />
                <input />
                <button>Send</button>
            </form>
            {isSent && (
                <h1>Thank you!</h1>
            )}
        </div>
    )
}

export default SendCoin
