import React, { useRef, useEffect } from 'react';
import Chartjs from 'chart.js';
import { historyOptions } from '../chart/chartconfig';
import styled from 'styled-components';

const ChartData = ({data}) => {
    const chartRef = useRef()
    const {day, detail} = data
    useEffect(() => {
        if (chartRef && chartRef.current && detail) {
            const chartInstance = new Chartjs(chartRef.current, {
                type: 'line',
                data: {
                    datasets: [{
                        label: `${detail.name}`,
                        data: day,
                        backgroundColor: "rgba(174, 305, 194, 0.5)",
                        borderColor: "rgba(173, 305, 194, 0.4)",
                        pointRadius: 0,
                    },]
                },
                options: {
                    historyOptions,
                },
            })
        }
    })


    return (
        <ChartContainer>
            {detail && (
                <>
                    <div>
                        <p>{detail.current_price.toFixed(2)}</p>
                        <p>{detail.price_change_percentage_24h.toFixed(2)}%</p>
                    </div>
                </>
            )}
            <canvas ref={chartRef} id="chart" width={250} height={250}></canvas>
        </ChartContainer>
    )
}

export default ChartData

const ChartContainer = styled.div`
    padding: 3rem;
    margin: 2rem;
    background-color: pink;
    width: 20rem;
`
