import React, { useEffect, useState, useContext } from 'react';
import CoinRow from './portfolio/CoinRow';
import { axiosInstance } from '../api/axios';
import { MyContext } from '../contextAPI/UserContext';
import axios from 'axios';
const Portfolio = () => {
    const [holdings, setHoldings] = useState([])
    const { user, coins } = useContext(MyContext)
    const [total, setTotal] = useState(null)
    const [res, setRes] = useState(null)
    const [mergedArr, setMergedArr] = useState(null)
    // const myArrayFilter = coins.filter(coin => {
    //     return holdings.some(holding => {
    //         return holding.name === coin.name
    //     })
    // })

    const createTotal = (arr) => {
        let total = 0
        arr.map((el) => (total += el.quantity * el.current_price))
        return total

    }

    // console.log(coins)
    useEffect(() => {
        async function getData() {
            try {
                let { data } = await axiosInstance.get(`/portfolio/`, {
                    params: {
                        id: 1
                    }
                })
                setHoldings(data)
                let str = ""
                data.map((e, i) => {
                    if (i !== data.length - 1) {
                        str += e.name.toLowerCase() + "%2C%20"
                    } else {
                        str += e.name.toLowerCase()
                    }
                })

                let res = await axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=${str}`)

                setRes(res.data)
            } catch (error) {
                console.log(error)
            }
        }
        getData()
    }, [])

    // const myArrayFilter = coins.filter(coin => {
    //     return holdings.some(holding => {
    //         return holding.name === coin.name
    //     })
    // })

    const click = () => {
        console.log(res)
        console.log(holdings)
        const a3 = res.map(t1 => ({ ...t1, ...holdings.find(t2 => t2.name === t1.name) }))
        setMergedArr(a3)
    }

    const click2 = () => {
        // const a3 = res.map(t1 => ({ ...t1, ...holdings.find(t2 => t2.name === t1.name) }))
        // setMergedArr(a3)
        let sum = mergedArr.reduce((a, b) => a + (b.quantity * parseFloat(b.current_price)), 0)
        setTotal(sum.toFixed(2))
        console.log(Math.round())
    }

    useEffect(() => {
        console.log(total)
        if (total !== null) {
            let sum = mergedArr.reduce((a, b) => a + (b.quantity * parseFloat(b.current_price)), 0)
            setTotal(sum.toFixed(2))
            console.log(Math.round())
        }
    })

    return (
        <>

            {holdings && (

                <div>
                    <button onClick={click}>Click m1e1</button>
                    <button onClick={click2}>Click me</button>
                    {/* <pre>{JSON.stringify(holdings, null, 2)}</pre> */}
                    {
                        total && <h1>${total}</h1>
                    }

                    <h1>Portfolio COmponent </h1>
                    {holdings?.map((holding, i) => (
                        <CoinRow
                            key={i}
                            name={holding.name}
                            symbol={holding.symbol}
                            quantity={holding.quantity}
                            image={holding.image_url}
                            id={holding.id}
                            user_id={holding.user}
                            holdings={holdings}
                            setHoldings={setHoldings}
                        />
                    ))}

                </div >
            )}
        </>
    )
}

export default Portfolio
