import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Chart from '../components/Chart';
import ChartData from '../components/ChartData';
import { coinGecko } from '../api/axios';
import axios from 'axios';

const Detail = () => {
    const { id } = useParams()
    const [ coinInfo, setCoinInfo ] = useState({})
    const [ isLoading, setIsLoading ] = useState(true)

    let urlPath = `coins/${id}/market_chart`

    const formatData = (data) => {
        return data.map(el => {
            return {
                t: el[0],
                y: el[1].toFixed(2)
            }
        })
    }
    useEffect(() => {
        async function getData() {
            const [ day, month, year, detail ] = await axios.all([
                coinGecko.get(urlPath, {
                    params: {
                        vs_currency: "usd",
                        days: "1",
                    }
                }),
                coinGecko.get(urlPath, {
                    params: {
                        vs_currency: "usd",
                        days: "30",
                    }
                }),
                coinGecko.get(urlPath, {
                    params: {
                        vs_currency: "usd",
                        days: "365"
                    }
                }),
                coinGecko.get('/coins/markets/', { params: { vs_currency: "usd", ids: id}})
            ])
            setCoinInfo({ 
                day: formatData(day.data.prices), 
                // week: formatData(week.data.prices),
                // month: formatData(month.data.prices), 
                // year: formatData(year.data.prices),
                detail: detail.data[0]
            });
            setIsLoading(!isLoading)
        }
        getData()
    }, [urlPath])

    return (
        <div>
            {isLoading ? (
                <>
                    <h1>Loading...</h1>
                </>
            ) : (
                <>
                    <Chart data={coinInfo}/>
                    <ChartData data={coinInfo}/>
                </>
            )} 
        </div>
    )
}

export default Detail
