import React from 'react';
import Plot from 'react-plotly.js';

const PlotData = ({data}) => {
    const transformData = (data) => {
        let plotData = []
        let x = []
        let y = []

        data.map(each => {
            let test = new Date(each.t).toLocaleTimeString("en-US")
            x.push(test)
            y.push(each.y)
        })
        plotData['x'] = x;
        plotData['y'] = y;

        return plotData
    }

    return (
        <div>
            <Plot data={[
                        {
                            type: 'scatter',
                            mode: 'lines',
                            x: transformData(data.day)['x'],
                            y: transformData(data.day)['y'],
                            marker: { color: '#ed022d'},
                        }
                    ]}
                        layout = {{width: 1000, height: 500, title: 'covid'}}
                    />
        </div>
    )
}

export default PlotData
