import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { popup } from '../../animations/animations'
import { motion } from 'framer-motion'

const Coin = ({ image, name, symbol, volume, price, priceChange, id }) => {
    return (
        <CoinContainer>
            <div className="row">
                <div className="coin">
                    <img src={image} alt="crypto" />
                    <Link to={`/cryptocurrencies/${id}`}>{name}</Link>
                    <p>{symbol}</p>
                </div>
                <div className="data">
                    <p className="price">${price.toLocaleString()}</p>
                    <p className="coin-volume">${volume.toLocaleString()}</p>
                    <Percent className="percent" priceChange={priceChange}>{priceChange}%</Percent>
                </div>
            </div>
        </CoinContainer>
    )
}

export default Coin

const CoinContainer = styled(motion.div)`
    display: flex;
    justify-content: center;

    .row {
        display: flex;
        flex-direction: row;
        justify-content: start;
        align-items: center;
        height: 80px;
        border-bottom: 1px solid black;
        width: 900px;
    }

    .coin {
        display: flex;
        align-items: center;
        padding-right: 24px;
        min-width: 300px;

        a {
            font-size: 16px;
            width: 150px;

        }

        img {
            height: 30px;
            width: 30px;
            margin-right: 10px;
        }

        p {
            text-transform: uppercase;
        }
    }

    .data {
        display: flex;
        text-align: center;
        justify-content: space-between;
        width: 100%;

        .price {
            width: 110px;
        }

        .coin-volume {
            width: 155px;
        }
    }


`

const Percent = styled.p`
    color: ${({ priceChange }) => (priceChange > 0 ? "green" : "red")}
`
