import {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';


function App() {
  const [coins, setCoins] = useState([])
  const [search, setSearch] = useState("")

  useEffect(() => {
    const getData = async () => {
      let {data} = await axios.get("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false")
      setCoins(data)
    }
    try {
      getData()
      console.log(coins)
    } catch (error) {
      console.log(error)
    }
  }, [])

  const onChangeHandler = (e) => {
    setSearch(e.target.value)
  }


  return (
    <div className="App">
      <div>
        <h1>Search currency</h1>
        <form>
          <input type="text" placeholder="Search" onChange={onChangeHandler}></input>
        </form>
      </div>
    </div>
  );
}

export default App;
