import React from 'react'
import NewsPost from './NewsPost';
import styled from 'styled-components';

const PostCollage = ({ posts, columns, tags }) => {
    // style={{ gridTemplateColumns: `repeat(${columns}), minmax(275px, 1fr))` }}
    return (

        <CollageContainer className="collage" style={{ gridTemplateColumns: `repeat(${columns}), minmax(275px, 1fr))` }} >

            {posts?.map((post, index) => (
                // <NewsPost className="post" {...{ post, index, tags, key: index }} />
                <div className="news__post overlay" key={index}>
                    <a href={post.image_url} className="collage-post">
                        <img src={post.image_url} alt="" />
                    </a>
                    <div className="image-text">
                        <div className="bottom">
                            <h2 className="image-title">{post.title}</h2>
                            <span>{post.date}</span>
                        </div>

                    </div>
                </div>
            ))}
        </CollageContainer>

    )
}

export default PostCollage

