import React from 'react';
import styled from 'styled-components';

const NewsCard = ({news}) => {
    console.log(news)
    return (
        <Posts>

                <div className="posts">
                    <img src={data.image_url} alt=""/>
                    <div className="posts__preview">

                    <h3>{data.title}</h3>
                    {/* <p>{data.source_name}</p> */}
                    <h6 className="date">{data.date}</h6>
                    <h5 className="text">{data.text}</h5>
                    </div>
                    <a target="_blank" rel={'external'} href='https://www.google.com' className="btn" href="">Read More</a>
                </div>


        </Posts>
    )
}

export default NewsCard


const Posts = styled.div`

    width: 95%;
    /* height: 270px; */
    height: 257px;
    margin: 20px auto;
    border-radius: 5px;
    background: lightgray;
    position: relative;

    img {
        width: 55%;
        height: 100%;
        float: left;
    }

    .post__preview {
        width: 60%;
        padding: 10px;
        /* float: right; */
        border: 1px solid green;

    }

    .btn {
        padding: .5rem 1rem;
        background: #006669;
        color: white;
        border: 1px solid transparent;
        /* border-radius: .25rem; */
        position: absolute;
        bottom: 10px;
        right: 10px;
        border: 1px solid #005255;
        background: transparent;
        color: #005255 !important;
        text-decoration: none;



        &:hover {
            color: white !important;
            background: #006669;
            transition: .25s;
        }
    }
`
