import React from 'react'
import { useForm } from 'react-hook-form';
import { axiosInstance } from '../../api/axios';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import styled from 'styled-components';

const schema = yup.object().shape({
    email: yup.string().required(),
    password: yup.string().min(4).max(15).required(),
    confirmPassword: yup.string().oneOf([yup.ref("password"), null])
})

const Register = () => {

    const { register, handleSubmit, formState: { errors } } = useForm({
        resolver: yupResolver(schema),
    })

    const registerHandler = async (data) => {
        try {
            const res = await axiosInstance.post('/user/create/', {
                "email": data.email,
                "password": data.password
            })
            return res
        } catch (error) {
            console.log(error.stack)
        }
    }

    return (
        <div>
            <Form onSubmit={handleSubmit(registerHandler)}>
                <section>
                    <h2>Sign Up</h2>
                    <div>
                        <p>Already have an account?
                            <a href="#">
                                <strong>Log In</strong>
                            </a>
                        </p>
                    </div>
                    <div className="input__container">
                        <label>
                            Email
                        <input type="text" {...register("email")} />
                        </label>
                        <p>{errors.email?.message}</p>
                        <label>
                            Password
                        <input type="password" placeholder="Must be at least 4 characters" {...register("password")} />
                        </label>
                        <p>{errors.password?.message}</p>
                        <label>
                            Retype password
                        <input type="password" {...register("confirmPassword")} />
                        </label>
                        <p>{errors.confirmPassword && "Passwords do not match"}</p>
                        <input className="btn" type="submit" value="submit" />
                        <section className="terms">
                            <p className="notice">By continuing, you agree to accept our <span className="links">Privacy Policy</span> & <span className="links">Cookie Use</span></p>
                        </section>
                    </div>
                </section>
            </Form>
        </div>
    )
}

export default Register

const Form = styled.form`
    width: 328px;

    input, label {
        display: block
    }

    section {
        color: black;
        text-align: center;

        p {
            margin: 1.5rem 0;
            font-size: 0.875rem;
        }


    }
    a {
        text-decoration: none;
        color: blue;

        &:hover {
            text-decoration: underline;
        }
    }

    .notice {
        font-size: 80%;
        text-align: center;
    }

    .links {
        color: blue;
    }

    .btn {
        display: block;
        width: 100%;
        background-color: blue;
        color: white;
        font-weight: 700;
        border: none;
        padding: 1rem;
        border-radius: 8px;
        font-size: 0.75rem;
    }
`
