import React from 'react'
import styled from 'styled-components';

const NewsPost = ({ post, tags }) => {
    console.log(post)
    // const style = { backgroundImage: `url(${post.image_url})` };

    const style = { ...post.style }
    return (
        <>
            <NewsPostContainer>
                <a href={post.image_url} className="collage-post overlay" style={style}>
                    <img src={post.image_url} alt="" />
                </a>
                <div className="image-text">
                    <div>
                        <h2 className="image-title">{post.title}</h2>
                        <span>{post.date}</span>
                    </div>

                </div>

            </NewsPostContainer>
        </>
    )
}

export default NewsPost

const NewsPostContainer = styled.div`
    position: relative;
    display: inline-block;
    text-align: center;
    color: white;

    img {
        width: 100%;
    }
    h2 {
        position: absolute;
        bottom: 8px;
        margin: 0 auto;
        left: 0;
        right: 0;
        top: 5%; /* adjust to move value up and down*/
        /* left: 16px; */
        text-align: center;
        width: 60%; /* adjust to wrap */
    }
    /* a {
        background-image: url(${props => props.image});
    } */
    .collage-post {
        position: relative;
        border-radius: 5px;
        overflow: hidden;
        background-size: cover;
        background-position: center center;
        height: 100%;
        width: 100%;
        text-decoration: none;
        margin: 0 auto;

    }
    .overlay::before {
        z-index: 1;
        content: '';
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        background: linear-gradient(180deg, transparent 0%, transparent 18%, rgba(0,0,0,0.8) 99%, rgba(0,0,0,0.8) 100%)
    }
`
