import React from 'react'
import styled from 'styled-components';

const NewsPost = ({ post, tags }) => {
    const style = { backgroundImage: `url(${post.image_url})` };
    return (
        <NewsPostContainer>
            <a image={post.image_url} style={style} className="collage-post overlay">
                <div className="image-text"></div>
                <div>
                    <h2 className="image-title"></h2>
                </div>
            </a>

        </NewsPostContainer>
    )
}

export default NewsPost

const NewsPostContainer = styled.div`
    /* a {
        background-image: url(${props => props.image});
    } */
    .collage-post {
        position: relative;
        border-radius: 5px;
        overflow: hidden;
        background-size: cover;
        background-position: center center;
        height: 100%;
        width: 100%;
        text-decoration: none;
        margin: 0 auto;

    }
    .overlay::before {
        z-index: 1;
        content: '';
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        background: linear-gradient(180deg, transparent 0%, transparent 18%, rgba(0,0,0,0.8) 99%, rgba(0,0,0,0.8) 100%)
    }
`
