import React from 'react'
import RegisterForm from '../components/users/RegisterForm';
import styled from 'styled-components';

const Register = () => {
    return (
        <RegisterContainer>
            <div className="left">
                <section className="description">
                    <h1>PLACEHOLDER TITLE</h1>
                    <p>I AM THE DESCRIPTION OF THE WEBSITE. CHANGE ME!</p>
                </section>
            </div>
            <div className="right">
                <RegisterForm />

            </div>
        </RegisterContainer>
    )
}

export default Register

const RegisterContainer = styled.div`
    display: flex;
    width: 100vw;
    /* flex-direction: column; */
    border: 1px solid green;
    text-align: left;
    padding: 0;
    margin: 0 !important;
    font-weight: 500;

    h1 {
        font-size: 2.25rem;
        font-weight: 700;
    }

    h2 {
        font-size: 1.5rem;
        font-weight: 700;
    }

    .left {
        height: 200px;
        width: 100%;
    }

    .right {
        width: 100%;
    }

    .left, .right {
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .left {
        // INSERT URL AFTER RGBA
        background: linear-gradient(rgba(0,0,0,0) rgba(0,0,0,0.5)), ;
        background-size: cover;
    }

    .description {
        color: yellow;
        text-align: center;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;

        p {
            font-weight: 400;
        }
    }

    @media screen and (min-width: 900px) {
        flex-direction: row;
        height: 100vh;

        .left, .right {
            display: flex;
            width: 50%auto;
            height: auto;
        }
    }

`
