import React, { useRef, useEffect, useState } from 'react';
import Chartjs from 'chart.js';
import { historyOptions } from '../chart/chartconfig';
import styled from 'styled-components';

const ChartData = ({data}) => {
    const chartRef = useRef()
    const {day, month, year, detail} = data
    const [timespan, setTimespan] = useState("24h")

    const determineTimespan = () => {
        switch (timespan) {
            case "24h":
                return day;
            case "30d":
                return month;
            case "1y":
                return year;
            default:
                return day;
        }
    }
    useEffect(() => {
        if (chartRef && chartRef.current && detail) {
            const chartInstance = new Chartjs(chartRef.current, {
                type: 'scatter',
                data: {
                    datasets: [{
                        label: `${detail.name}`,
                        data: determineTimespan(),
                        backgroundColor: "rgba(174, 305, 194, 0.5)",
                        borderColor: "rgba(173, 305, 194, 0.4)",
                        pointRadius: 0,
                    },]
                },
                options: {
                    historyOptions,
                },
            })
        }
    })


    return (
        <ChartContainer>
            {detail && (
                <>
                    <div>
                        <p>{detail.current_price.toFixed(2)}</p>
                        <PriceChange price={detail.price_change_percentage_24h}>{detail.price_change_percentage_24h.toFixed(2)}%</PriceChange>
                    </div>
                </>
            )}
            <canvas ref={chartRef} id="chart" width={250} height={250}></canvas>
            <div>
                <button onClick={() => setTimespan("24h")}>1 Day</button>
                <button onClick={() => setTimespan("30d")}>1 Month</button>
                <button onClick={() => setTimespan("1y")}>1 Year</button>
            </div>
        </ChartContainer>
    )
}

export default ChartData

const ChartContainer = styled.div`
    padding: 3rem;
    margin: 2rem;
    background-color: pink;
    width: 20rem;
`

const PriceChange = styled.p`
    color: ${({price}) => (price > 0 ? "green" : "red")};
`
