import React, { useState } from 'react';
import Plot from 'react-plotly.js';
import styled from 'styled-components';

const PlotData = ({ data }) => {
    const [timespan, setTimespan] = useState("24")
    const { day, month, year, detail } = data
    // console.log(detail)
    const timeViewHandler = () => {
        switch (timespan) {
            case "24h":
                return day;
            case "30d":
                return month;
            case "1y":
                return year;
            default:
                return day;
        }
    }

    const transformData = (data) => {
        let plotData = []
        let x = []
        let y = []

        data?.forEach(each => {
            // let test = new Date(each.t).toLocaleTimeString("en-US")
            // x.push(test)
            x.push(each.t)
            y.push(each.y)
        })
        plotData['x'] = x;
        plotData['y'] = y;

        return plotData
    }

    return (
        <div>
            {detail && (
                <>
                    <div>
                        <p>${detail.current_price.toLocaleString()}</p>
                        <PriceChange price={detail.price_change_percentage_24h}>{detail.price_change_percentage_24h.toFixed(2)}%</PriceChange>
                    </div>
                </>
            )}
            {
                day && (
                    <>
                        <Plot data={[
                            {
                                type: 'scatter',
                                mode: 'lines',
                                x: transformData(timeViewHandler())['x'],
                                y: transformData(timeViewHandler())['y'],
                                marker: { color: '#ed022d' },
                            }
                        ]}
                            layout={{ autosize: true, title: `${detail.name}` }}
                            useResizeHandler={true}
                            style={{ width: "100%", height: "100%" }}
                        />
                        <Buttons>
                            <button onClick={() => setTimespan("24h")}>1 Day</button>
                            <button onClick={() => setTimespan("30d")}>1 Month</button>
                            <button onClick={() => setTimespan("1y")}>1 Year</button>
                        </Buttons>
                    </>
                )
            }

        </div>
    )
}

export default PlotData

const PriceChange = styled.p`
    color: ${({ price }) => (price > 0 ? "green" : "red")};
`

const Buttons = styled.div`
    display: flex;
    /* align-items: center; */
    justify-content: space-between;
    padding-left: 10rem;
    padding-right: 10rem;
`
