import React, { useState } from 'react';
import Plot from 'react-plotly.js';
import styled from 'styled-components';

const PlotData = ({ data }) => {
    const [timespan, setTimespan] = useState("24")
    const [isActiveOne, setIsActiveOne] = useState({ isActiveOne: true, isActiveTwo: false, isActiveThree: false })
    const [isActiveTwo, setIsActiveTwo] = useState({ isActiveOne: false, isActiveTwo: false, isActiveThree: false })
    const [isActiveThree, setIsActiveThree] = useState({ isActiveOne: false, isActiveTwo: false, isActiveThree: false })
    const { day, month, year, detail } = data



    const timeViewHandler = () => {
        switch (timespan) {
            case "24h":
                return day;
            case "30d":
                return month;
            case "1y":
                return year;
            default:
                return day;
        }
    }

    const transformData = (data) => {
        let plotData = []
        let x = []
        let y = []

        data?.forEach(each => {
            x.push(each.t)
            y.push(each.y)
        })
        plotData['x'] = x;
        plotData['y'] = y;

        return plotData
    }

    const dayBtn = () => {
        setTimespan("24h")
        setIsActiveOne({ isActiveOne: true, isActiveTwo: false, isActiveThree: false })
    }
    const monthBtn = () => {
        setTimespan("30d")
        setIsActiveTwo({ isActiveOne: false, isActiveTwo: true, isActiveThree: false })
    }
    const yearBtn = () => {
        setTimespan("1y")
        setIsActiveThree({ isActiveOne: false, isActiveTwo: false, isActiveThree: true })
    }

    return (
        <div>
            {detail && (
                <>
                    <div>
                        <p>${detail.current_price.toLocaleString()}</p>
                        <PriceChange price={detail.price_change_percentage_24h}>{detail.price_change_percentage_24h.toFixed(2)}%</PriceChange>
                    </div>
                </>
            )}
            {
                day && (
                    <>
                        <Plot data={[
                            {
                                type: 'scatter',
                                mode: 'lines',
                                x: transformData(timeViewHandler())['x'],
                                y: transformData(timeViewHandler())['y'],
                                marker: { color: '#ed022d' },
                            }
                        ]}
                            layout={{ autosize: true, title: `${detail.name}` }}
                            useResizeHandler={true}
                            style={{ width: "100%", height: "100%" }}
                        />
                        <Buttons>
                            <button isActiveOne={isActiveOne} className="btn one" onClick={dayBtn}>1 Day</button>
                            <button isActiveTwo={isActiveTwo} className="btn two" onClick={monthBtn}>1 Month</button>
                            <button isActiveThree={isActiveThree} className="btn three" onClick={yearBtn}>1 Year</button>
                        </Buttons>
                    </>
                )
            }
        </div>
    )
}

export default PlotData

const PriceChange = styled.p`
    color: ${({ price }) => (price > 0 ? "green" : "red")};
`

const Buttons = styled.div`
    .btn {
        margin: 0 8px 0 8px;
        padding: 2px 8px;
    }

    .one {
        background-color: ${({ isActiveOne }) => isActiveOne ? 'green' : 'red'}
    }
    .two {
        background-color: ${({ isActiveTwo }) => isActiveTwo ? 'green' : 'red'}
    }
    .three {
        background-color: ${({ isActiveThree }) => isActiveThree ? 'green' : 'red'}
    }

`
