import React, { useState, useEffect} from 'react';
import { coinGecko } from '../api/axios';
import Coin from '../components/Coin';
import styled from 'styled-components';
const CryptoData = () => {
  const [coins, setCoins] = useState([])
  const [search, setSearch] = useState("")

  useEffect(() => {
    const getData = async () => {
      let {data} = await coinGecko.get("/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false")
      setCoins(data)
    }
    try {
      getData()
    } catch (error) {
      console.log(error)
    }
  }, [])

  const filteredCoins = coins.filter(coin => coin.name.toLowerCase().includes(search.toLowerCase()))
  const onChangeHandler = (e) => {
    setSearch(e.target.value)
  }

    return (
        <CryptoContainer>
            <div>
        <div className="form__container">

        <form class="form">
        <h1>Search currency</h1>
          <div className="form__div">

            <input name="search" className="form__input" type="text" onChange={onChangeHandler}></input>
            <label className="form__label" hmtlFor="search">Search</label>
          </div>
        </form>
        </div>
      </div>
      {filteredCoins.map(coin => {
        return (
          <Coin key={coin.id}
          id={coin.id}
          name={coin.name} 
          image={coin.image} 
          symbol={coin.symbol} 
          volume={coin.market_cap} 
          price={coin.current_price}
          priceChange={coin.price_change_percentage_24h}
          />
        )
      })}
        </CryptoContainer>
    )
}

export default CryptoData

const CryptoContainer = styled.div`

  /* .form__container {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  form {
    width: 360px;
    padding: 4rem 2rem;
    border-radius: 1rem;
  }

  .form__title {
    position: relative;
    height: 48px;
    margin-bottom: 1.5rem;
  } */
  .form__container {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .form {
    width: 360px;
    /* padding: 4rem; */

  }
  .form__div {
    position: relative;
    height: 48px;
    margin-bottom: 1.5rem;
  }

  .form__input {
    position: absolute;
    top: 0;
    /* left: 0; */
    height: 12px;
    /* width: 100 */
    padding: 8px;
    border-radius: 4px;
    border: 1px solid black;
    outline: none;
    padding: 1rem;
    background: none;
    z-index: 1;
  }

  .form__label {
    position: absolute;
    /* left: 1rem; */
    top: 1rem;
    padding: 0 .25rem;
    background-color: #fff;
    color: blue;
    font-size: 12px;
    transition: .3s;
    font-size: 1rem;
  }

  .form__input:focus + .form__label{
    top: -0.5rem;
    /* left: .8rem; */
    color: purple;
    font-size: 1rem;
    z-index: 10;
  }

`
