import React, { useState, useEffect} from 'react';
import NewsCard from '../components/News/NewsCard'
import axios from 'axios';
import styled from 'styled-components';

const News = () => {
    const [news, setNews] = useState([])

    useEffect(() => {
        async function getData() {
          let { data } = await axios.get('https://cryptonews-api.com/api/v1/category?section=general&items=50&token=vpwjwlmbytww0hjfywdtmlh29vkeugws7cesanuj')
          setNews(data)
        }
        getData()
      }, []);

    return (
        <div>

        <NewsCardContainer>
            <div className="main__content">
                <h1>Crypto News</h1>
                {news.data?.map((article) => (
                  <NewsCard data={article}/>
        ))}

            </div>
            {/* <div className="sidebar"></div> */}
        </NewsCardContainer>
        </div>
    )
}

export default News

const NewsCardContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;

  .row {
    margin: 4em 20px;
  }

  .overlay::before {
    z-index: 1;
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: linear-gradient(180deg, transparent 0%, transparent 18%, rgba(0,0,0,0.8) 99%, rgba(0,0,0,0.8) 100%)
  }
`
