        //             </div>
        //             <button>Send</button>
        //         </form>
        //         {isSent && (
        //             <h1>Thank you!</h1>
        //         )}
        //     </div>
        //     <h1>Address Book</h1>
        //     <div className="address__book">
        //         {addresses.map((address) => (
        //             <div className="address">
        //                 <p>{address}</p>
        //             </div>
        //         ))}
        //     </div>
        // </Container>
        <PageContainer>
            <h1 className="transaction">Make a Transaction</h1>
            <FormContainer>
                <form onSubmit={submitHandler}>
                    {/* <h1 className="signin">Sign In</h1> */}

                    <div className="inputs">
                        <input className="form__input" type="text" placeholder=" " name="recipient" />
                        <label htmlFor="recipient">Recipient</label>
                    </div>

                    <div className="inputs">
                        <input className="form__input" type="password" placeholder=" " name="amount" />
                        <label htmlFor="amount">Amount</label>
                    </div>
                    <input className="btn" type="submit" value="SEND" />
                </form>
            </FormContainer>
            <div className="address__book">
                {addresses.map((address) => (
                    <p>{address}</p>
                ))}
            </div>
        </PageContainer>
    )
}

export default SendCoin

