import React, { useState, useEffect } from 'react';
import NewsCard from '../components/News/NewsCard'
import axios from 'axios';
import styled from 'styled-components';
import PostCollage from '../components/PostCollage.js'
import FeaturedPosts from '../components/FeaturedPosts.js'
import NewsPost from '../components/NewsPost';
const News = () => {
  const [news, setNews] = useState([])

  useEffect(() => {
    async function getData() {
      let { data } = await axios.get('https://cryptonews-api.com/api/v1/category?section=general&items=5&token=vpwjwlmbytww0hjfywdtmlh29vkeugws7cesanuj')
      setNews(data.data)
    }
    getData()
  }, []);

  return (
    <div>
      {news && (<NewsCardContainer>
        <div className="row">
          <h2>Featured Posts</h2>
          <section className="featured__posts">
            <FeaturedPosts posts={news} />
            <NewsPost />
          </section>
          <h2>Bitcoin</h2>
          <PostCollage posts={news} />
        </div>

      </NewsCardContainer>)}
    </div>
  )
}

export default News

const NewsCardContainer = styled.section`
  max-width: 1200px;
  margin: 0 auto;

  h2{
    width: 100%;
    color: white;

  }

  .row {
    margin: 4em 20px;
  }

  .overlay::before {
    z-index: 1;
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: linear-gradient(180deg, transparent 0%, transparent 18%, rgba(0,0,0,0.8) 99%, rgba(0,0,0,0.8) 100%)
  }

  .featured__posts {
    display: flex;
  }
`
