import React, { useState, useEffect } from 'react';
import NewsCard from '../components/News/NewsCard'
import axios from 'axios';
import styled from 'styled-components';
import PostCollage from '../components/PostCollage.js'


const News = () => {
  const [news, setNews] = useState([])

  const trendingConfig = {
    1: {
      gridArea: '1 / 2 / 3 / 3'
    }
  }

  const mergeStyles = function (posts, config) {
    let newPosts = [...posts]
    newPosts?.forEach((post, index) => {
      post.style = config[index]
    })
    setNews(newPosts)

  }

  // mergeStyles(news, trendingConfig)

  useEffect(() => {
    async function getData() {
      let { data } = await axios.get('https://cryptonews-api.com/api/v1/category?section=general&items=6&token=vpwjwlmbytww0hjfywdtmlh29vkeugws7cesanuj')
      setNews(data.data)
      mergeStyles(news, config)
    }
    getData()
  }, []);
  console.log(typeof news)
  console.log(news)
  // mergeStyles(news?.data, trendingConfig)
  return (
    <div>
      {news && (<NewsCardContainer>
        <div className="row">
          <h2>Bitcoin</h2>
          <PostCollage posts={news} columns={3} />
          {/* <div className="main__content">
          <h1>Crypto News</h1>
          {news.data?.map((article) => (
            <NewsCard data={article} />
          ))}

        </div> */}
        </div>

      </NewsCardContainer>)}
    </div>
  )
}

export default News

const NewsCardContainer = styled.section`
  max-width: 1200px;
  margin: 0 auto;

  h2{
    width: 100%;
    color: blue;

  }

  .row {
    margin: 4em 20px;
  }

  .overlay::before {
    z-index: 1;
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: linear-gradient(180deg, transparent 0%, transparent 18%, rgba(0,0,0,0.8) 99%, rgba(0,0,0,0.8) 100%)
  }
`
