import React, { useState } from 'react'
import styled from 'styled-components'
import { axiosInstance } from '../../api/axios'
import { useHistory } from 'react-router-dom';

const CoinRow = ({ name, symbol, quantity, image, id, user_id }) => {
    const [isDeleted, setIsDeleted] = useState(false)
    const [isEdit, setIsEdit] = useState(false)

    const editHandler = (e) => {
        e.preventDefault()

    }

    const deleteHandler = async (e) => {
        e.preventDefault()
        try {
            let response = await axiosInstance.delete(`portfolio/${id}/`)
            console.log(response)
            setIsDeleted(true)
        } catch (error) {
            console.log(error)
        }
    }
    return (
        <CoinRowContainer isDeleted={isDeleted}>
            <div className="row">
                <img src={image} alt="" />
                <a>{name}</a>
                <p>{symbol}</p>
                <p>{quantity}</p>
            </div>
            <div className="btns">
                <button>Edit</button>
                <form onSubmit={editHandler}>
                    <input type="text" />
                    <button>Edit</button>
                </form>

                <form onSubmit={deleteHandler}>
                    <button>Delete</button>
                </form>
            </div>

        </CoinRowContainer>
    )
}

export default CoinRow

const CoinRowContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    display: ${({ isDeleted }) => isDeleted ? 'none' : ''};


    .row {
        display: flex;
        align-items: center;
        /* padding-right: 24px; */

        a {
            font-size: 16px;
            width: 150px;
        }

        img {
            height: 30px;
            width: 30px;
            margin-right: 10px;
        }

    }

    .btns {
        display: flex;
        align-items: center;
    }
`
