    grid-template-areas:  */

    &:nth-child(2){
        grid-area: 1 / 2 / 3 / 3;
    }

    // REMINDER MIGHT NOT NEED THE BOTTOM
    .collage {
        /* display: grid; */
        display: flex;
        /* grid-gap: 30px; */
        flex-direction: column;
        /* grid-template-columns: minmax(275px, 1fr), repeat(3); */

    }


`
