import React, { useState, useEffect} from 'react';
import axios from 'axios';
import Coin from '../components/Coin';

const CryptoData = () => {
  const [coins, setCoins] = useState([])
  const [search, setSearch] = useState("")

  useEffect(() => {
    const getData = async () => {
      let {data} = await axios.get("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false")
      setCoins(data)
    }
    try {
      getData()
    } catch (error) {
      console.log(error)
    }
  }, [])

  const filteredCoins = coins.filter(coin => coin.name.toLowerCase().includes(search.toLowerCase()))
  const onChangeHandler = (e) => {
    setSearch(e.target.value)
  }

    return (
        <div>
            <div>
        <h1>Search currency</h1>
        <form>
          <input type="text" placeholder="Search" onChange={onChangeHandler}></input>
        </form>
      </div>
      {filteredCoins.map(coin => {
        return (
          <Coin key={coin.id} 
          name={coin.name} 
          image={coin.image} 
          symbol={coin.symbol} 
          volume={coin.market_cap} 
          price={coin.current_price}
          priceChange={coin.price_change_percentage_24h}
          />
        )
      })}
        </div>
    )
}

export default CryptoData
