import { UserContext } from '../../contextAPI/UserContext';
import { axiosInstance } from '../../api/axios';

const CoinData = ({ data }) => {
    const { user } = useContext(UserContext)

    const sendData = (e) => {
        e.preventDefault()
        // let response = await axiosInstance(`/portfolio/`, {
        //     params: {
        //         id: user
        //     }
        // })
        console.log(user)
    }


    return (
        <div>
            {data && (<div>
                {/* <pre>{JSON.stringify(data,null,2)}</pre> */}
                <p>24h High ${data.high_24h.toLocaleString()}</p>
                <p>24h Low ${data.low_24h.toLocaleString()}</p>
                <p>Market Cap {data.market_cap}</p>
                <p>Market Cap Change {data.market_cap_change_percentage_24h.toFixed(2)}%</p>
                <p>Circulating Supply {data.circulating_supply}</p>
                <p>Max Supply {data.max_supply}</p>
                {user && (
                    <form onSubmit={sendData}>
                        <input type="text" placeholder="amount" name="portfolio" />
                        <button>Add</button>
                    </form>
                )}
            </div>)}
        </div>
    )
}

export default CoinData
