import React from 'react'
import NewsPost from './NewsPost';

const PostCollage = ({ posts, columns, tags }) => {
    return (
        <section className="collage" style={{ gridTemplateColumns: `repeat(${columns}), minmax(275px, 1fr))` }}>
            {posts?.map((post, index) => (
                <NewsPost {...{ post, index, tags, key: index }} />
            ))}
        </section>
    )
}

export default PostCollage
