import React, { useEffect, useState, useContext } from 'react'
import { axiosInstance } from '../api/axios';
import Carbchain from '../components/blockchain/Carbchain';
import styled from 'styled-components'
import coin from '../images/favi.png'
import { MyContext } from '../contextAPI/UserContext'

const Blockchain = () => {
    const [wallet, setWallet] = useState([])
    const { user } = useContext(MyContext)
    useEffect(() => {
        async function getData() {
            let { data } = await axiosInstance.get('http://localhost:8000/api/wallet/balance/')
            setWallet(data)
        }
        getData()
    }, [])

    return (
        <BlockchainContainer>
            {/* <pre>{JSON.stringify(wallet, null, 2)}</pre> */}
            <div className="page__title">
                <img src={coin} alt="" />
                <h1 className="carbchain">Carbchain</h1>
            </div>
            {user && (
                <div className="header">
                    <h1>Address: <span>{wallet.address}</span></h1>
                    <h1>Balance: <span>{wallet.balance}</span></h1>
                </div>
            )}
            <Carbchain />
        </BlockchainContainer>
    )
}

export default Blockchain

const BlockchainContainer = styled.section`
    /* background-color: gray; */
    padding: 0.5rem calc((100vw - 1200px) / 2);
    border: none;
    /* background-color: #121212; */

    .page__title {
        width: 100%;
        display: flex; 
        align-items: center;
        /* text-align: center; */
        justify-content: center;

        img {
            width: 2.3rem;
            transform: translateY(8px);
            padding-right: 0.5rem;
        }
        .carbchain {
            font-size: 2.5rem;
            margin-top: 3rem;
            /* font-size: 2rem; */
            font-weight: 700;
            whit
        }
    }

    .header {
        font-size: 1rem;
        margin-top: 4rem;
        margin-bottom: 4rem;
    }

    span {
        font-weight: 400;
    }
`
