import styled from "styled-components";
import Burger from "./Burger";
import { Link } from 'react-router-dom';
// import Logo from "../Logo";

const NavBar = () => {
  return (

    <NavBarContainer>
      <Link>
        {/* <Logo /> */}
        <h1>Click</h1>
      <NavItems>
        <Link to>News</Link>
        {/* <Link>Wallet</Link> */}
        <Link>CarbChain</Link>
        <Link>Cryptocurrencies</Link>
      </NavItems>
      </Link>
      <Burger />
      {/* <div></div> */}
      <div>
          <Link>Login</Link>
      </div>
    </NavBarContainer>

  );
};

const NavBarContainer = styled.div`
  /* margin-top: -80px; // might wanna adjsut his for future projects */
  background-color: #000;
  /* background: transparent; */
  color: red;
  /* width: 100%; DO NOT INCLUDE THIS IF YOU ARE USING CALC!!!!! */
  height: 64px;
  display: flex;
  justify-content: space-between;
  padding: 0.5rem calc((100vw - 1000px) / 2); /* THIS IS THE MAGIC!!!!!!!!!!!! */
  z-index: 10;
`;

const NavLink = styled.a`
  color: red;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 2rem;
  height: 100%;
  cursor: pointer;

  &.active {
    color: #15cdfc;
  }
`;

const NavItems = styled.div`
  display: flex;
  align-items: center;

  a {
    color: red;
    display: flex;
    align-items: center;
    text-decoration: none;
    padding: 0 2rem;
    height: 100%;
    cursor: pointer;

  &.active {
    color: #15cdfc;
  }
  }

  @media screen and (max-width: 768px) {
    display: none;
  }
`;
export default NavBar;
