import React from 'react'
import NewsPost from './NewsPost';
import styled from 'styled-components';

const PostCollage = ({ posts, columns, tags }) => {
    // style={{ gridTemplateColumns: `repeat(${columns}), minmax(275px, 1fr))` }}
    return (

        <CollageContainer className="collage" style={{ gridTemplateColumns: `repeat(${columns}), minmax(275px, 1fr))` }} >

            {posts?.map((post, index) => (
                // <NewsPost className="post" {...{ post, index, tags, key: index }} />
                <NewsPostContainer>
                    <a href={post.image_url} className="collage-post overlay" style={style}>
                        <img src={post.image_url} alt="" />
                    </a>
                    <div className="image-text">
                        <div>
                            <h2 className="image-title">{post.title}</h2>
                            <span>{post.date}</span>
                        </div>

                    </div>
                </NewsPostContainer>
            ))}
        </CollageContainer>

    )
}

export default PostCollage

const CollageContainer = styled.section`
    display: grid;
    grid-gap: 30px;
    grid-template-columns: minmax(275px, 1fr) minmax(275px, 1fr) minmax(275px, 1fr);
    /* grid-template-rows: 1fr 1fr
    grid-template-areas:  */

    .post:nth-child(2){
        grid-area: 1 / 2 / 3 / 3;
    }

    // REMINDER MIGHT NOT NEED THE BOTTOM
    .collage {
        /* display: grid; */
        display: flex;
        /* grid-gap: 30px; */
        flex-direction: column;
        /* grid-template-columns: minmax(275px, 1fr), repeat(3); */

    }


`
