import React, { useState } from 'react'
import styled from 'styled-components'
import { axiosInstance } from '../../api/axios'
import { useHistory } from 'react-router-dom';

// IF TIME SET UP REDUCERS FOR DELETE AND EDIT TO 
const CoinRow = ({ name, symbol, quantity, image, id, user_id }) => {


    const [isDeleted, setIsDeleted] = useState(false)
    const [isEdit, setIsEdit] = useState(false)
    const [_quantity, setQuantity] = useState(quantity)

    const saveEditHandler = async (e) => {
        e.preventDefault()
        try {
            console.log(id)
            let response = await axiosInstance.put(`portfolio/${id}/`, {
                name,
                symbol,
                "quantity": e.target[0].value,
                image,
                user: user_id
            })
            console.log(response)
            // console.log(e.target[0].value)
            // let me = {
            //     name,
            //     symbol,
            //     "quantity": e.target[0].value,
            //     image,
            //     user: user_id
            // }
            // console.log(me)
            setIsEdit(!isEdit)

        } catch (error) {
            console.log(error)
        }

    }

    const deleteHandler = async (e) => {
        e.preventDefault()
        try {
            let response = await axiosInstance.delete(`portfolio/${id}/`)
            console.log(response)
            setIsDeleted(true)
        } catch (error) {
            console.log(error)
        }
    }

    const editHandler = () => {
        setIsEdit(!isEdit)
    }

    return (
        <CoinRowContainer isDeleted={isDeleted}>
            <div className="row">
                <img src={image} alt="" />
                <a>{name}</a>
                <p>{symbol}</p>
                {!isEdit && (

                    <p className="quantity" isEdit={isEdit}>{_quantity}</p>
                )}
            </div>
            <div className="btns">

                {isEdit && (
                    <form className="save__edit" onSubmit={saveEditHandler} isEdit={isEdit}>
                        <input defaultValue={_quantity} className="edit__input" type="text" />
                        <button>Save</button>
                    </form>

                )}
                {!isEdit && (
                    <button onClick={editHandler}>Edit</button>

                )}

                {/* DONE */}
                <form onSubmit={deleteHandler}>
                    <button>Delete</button>
                </form>
            </div>

        </CoinRowContainer>
    )
}

export default CoinRow

const CoinRowContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    display: ${({ isDeleted }) => isDeleted ? 'none' : ''};

    /* .quantity {
        display: ${({ isEdit }) => isEdit ? 'none' : ''};
    }

    .save__edit {
        display: ${({ isEdit }) => isEdit ? 'none' : ''}
    }

    .testing {
        display: ${({ isEdit }) => isEdit ? 'none' : ''}

    } */

    .row {
        display: flex;
        align-items: center;
        /* padding-right: 24px; */

        a {
            font-size: 16px;
            width: 150px;
        }

        img {
            height: 30px;
            width: 30px;
            margin-right: 10px;
        }

    }

    .btns {
        display: flex;
        align-items: center;
    }
`
