import React, { useState, useEffect } from 'react'
import { axiosInstance } from '../api/axios'
import { useHistory } from 'react-router-dom'
import styled from 'styled-components'

const SendCoin = () => {
    const [isSent, setIsSent] = useState(false)
    const [addresses, setAddresses] = useState([])
    let history = useHistory()

    useEffect(() => {
        async function getData() {
            try {
                let { data } = await axiosInstance.get('blockchain/addresses/')
                setAddresses(data)
                console.log(data)
            } catch (error) {
                console.log(error)
            }
        }
        getData()
    }, [])
    const submitHandler = async (e) => {
        e.preventDefault()
        try {
            let response = await axiosInstance.post('blockchain/transaction/', {
                recipient: e.target[0].value,
                amount: Number(e.target[1].value)
            })
            if (response.status === 200) {
                history.push('/blockchain/transactions')
            }

        } catch (error) {
            console.log(error)
        }
    }

    return (
        <Container>
            <h1>Address Book</h1>

            {/* <pre>{JSON.stringify(addresses, null, 2)}</pre> */}
            <div className="form__container" >
                <form onSubmit={submitHandler}>
                    <div>
                        <input name="recipient" />
                        <label htmlFor="recipient">Recipient</label>
                    </div>
                    <div>
                        <input />
                        <label htmlFor="recipient">Recipient</label>
                    </div>
                    <button>Send</button>
                </form>
                {isSent && (
                    <h1>Thank you!</h1>
                )}
            </div>
            <div className="address__book">
                {addresses.map((address) => (
                    <div className="address">
                        <p>{address}</p>
                    </div>
                ))}
            </div>
        </Container>
    )
}

export default SendCoin

const Container = styled.div`
    display: flex;
    flex-direction: column;
    padding: 0.5rem calc((100vw - 1200px) / 2);

    .form__container {
        padding-top: 5rem;
        /* display: flex;
        flex-direction: column; */
        /* width: 20%; */
        border: 1px solid gray;

        form {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        }
    }
    .address__book {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;

        .address {
            color: blue;
            padding-left: 4px; 
            padding-right: 4px;
        }
    }
`
